<template>
    <view class="content">
		<uni-fab
		:pattern="pattern"
		:content="content"
		:horizontal="horizontal"
		:vertical="vertical"
		:direction="direction"
		:type="type"
		@trigger="trigger"
		></uni-fab>
		<view class="container">
				<view class="address-list">
					<view class="a-address" v-for="(item,index) in addressList" :key="index">
						<view class="left-text" @tap="selectTap(item.id)">
							<view class="name-tel">
								{{item.date}}        {{item.data}}
							</view>
							<view class="address-box">
								{{item.address}}
							</view>
						</view>
						<view class="right-edit" @tap="editAddess(item.id)"></view>
					</view>
				</view>
		</view>
    </view>
</template>

<script>
    import {
        mapState
    } from 'vuex'
	import uniFab from '@/components/uni-fab.vue';
    import dataBase from '../../dataBase.js';
	
    export default {
        computed: mapState(['forcedLogin', 'hasLogin', 'userName']),
        onLoad() {
            if (!this.hasLogin) {
                uni.showModal({
                    title: '未登录',
                    content: '您未登录，需要登录后才能继续',
                    /**
                     * 如果需要强制登录，不显示取消按钮
                     */
                    showCancel: !this.forcedLogin,
                    success: (res) => {
                        if (res.confirm) {
							/**
							 * 如果需要强制登录，使用reLaunch方式
							 */
                            if (this.forcedLogin) {
                                uni.reLaunch({
                                    url: '../login/login'
                                });
                            } else {
                                uni.navigateTo({
                                    url: '../login/login'
                                });
                            }
                        }
                    }
                });
            }
        },
		onShow() {
			if(this.hasLogin){
				this.addressList = dataBase.getDataBase(this.userName).reverse();
			}
		},
		 data() {
        return {
            horizontal: 'left',
            vertical: 'bottom',
            direction: 'horizontal',
            pattern: {
                color: '#7A7E83',
                backgroundColor: '#fff',
                selectedColor: '#007AFF',
                buttonColor:"#007AFF"
            },
			type:"water",
			addressList: [{
				id:1,
				date:"未登录",
				data:'请在登陆后使用',
				address:'未登录',
			}],
        };
    },
    methods: {
        trigger(e) {
			
        },
		selectTap(id) {
			console.log("tap item id:" + JSON.stringify(id));
		},
		editAddess(id) {
			console.log("edit item id:" + id);
			uni.navigateTo({
				url:"../add-page/add-page?msg=water&type=change&id="+id,
			})
		},
		addAddess() {
			console.log("tap add new Address");
		}
    },
    components: {
        uniFab
    }

    }
</script>

<style>

    .title {
        color: #8f8f94;
        margin-top: 50upx;
    }

    .ul {
        font-size: 30upx;
        color: #8f8f94;
        margin-top: 50upx;
    }

    .ul>view {
        line-height: 50upx;
    }
	.container {
		background-color: #F2f2f2;
	}
	
	.address-list {
		width: 100%;
		background-color: #fff;
		margin-top: 20upx;
		padding-bottom: 100upx;
	}
	
	.address-list .a-address {
		margin-left: 30upx;
		display: flex;
		justify-content: space-between;
		align-items: center;
		padding: 30upx 0;
		border-bottom: 1upx solid #eee;
	}
	
	.a-address .left-text {
		width: 510upx;
		box-sizing: border-box;
		padding-left: 20upx;
	}
	
	
	.a-address .left-text .name-tel {
		margin-bottom: 20upx;
	}
	
	.a-address .left-text .address-box {
		font-size: 24upx;
		color: #888888;
		line-height: 36upx;
	}
	
	.a-address .right-edit {
		width: 90upx;
		height: 100%;
		padding: 50upx 0 50upx 58upx;
		box-sizing: border-box;
		background: url(data:image/png;base64,) no-repeat 43upx center;
		background-size: 35upx auto;
	}
	
	.bottom-box {
		position: fixed;
		width: 100%;
		bottom: 0;
		font-size: 28upx;
		color: #000000;
		border-top: 1upx solid #eee;
		height: 100upx;
		line-height: 100upx;
		background: #fff url(data:image/png;base64,) no-repeat 705upx center;
		background-size: 15upx auto;
	}
	
	.bottom-box .add-btn {
		margin-left: 30upx;
		padding-left: 70upx;
		background: url(data:image/png;base64,) no-repeat left center;
		background-size: 40upx auto;
	}
</style>
